import { TextField, Button } from "@material-ui/core";
import { Rating } from "@material-ui/lab";
import axios from "axios";
import React, { useState } from "react";

const ShowInfo = (props) => {
  const [rating, setRating] = useState(props.userInfo.rating);
  const uploadFile = (event) => {
    let type = event.target.name;
    let filType = event.target.files[0].type;
    if (
      type === "Image" &&
      filType !== "image/jpeg" &&
      filType !== "image/png"
    ) {
      alert("Only jpeg and png image allowed");
    } else {
      let data = new FormData();
      data.append("file", event.target.files[0]);
      data.append("type", type);
      axios.defaults.withCredentials = true;
      axios
        .post("http://localhost:8080/storeFile", data, {
          headers: { "Content-Type": "multipart/form-data" },
        })
        .then((response) => alert(type + "Uploaded"))
        .catch((err) => {
          alert("Couldn't upload ");
          console.log(err);
        });
      window.location.reload();
    }
  };
  const displayImage = () => {
    let images = require.context("../../../public/images", true);
    const image = images("./profilePic.png");
    return (
      <img src='./profilePic.png' style={{ width: 100 }} alt='profilePooooic' />
    );
  };
  return (
    <div>
      {displayImage()}
      <TextField value={props.userInfo.name}></TextField>
      <br />
      <TextField value={props.userInfo.email}></TextField> <br />
      <Rating
        name='half-rating'
        value={rating}
        onChange={(event, value) => {
          setRating(value);
        }}
        defaultValue={props.userInfo.rating}
        precision={0.5}
      />{" "}
      <h1>{rating}</h1>
      <br />
      <TextField value={props.userInfo.resumePath}></TextField> <br />
      <Button variant='contained' component='label'>
        Upload Image
        <input
          type='file'
          name='Image'
          hidden
          onChange={(e) => (e.target ? uploadFile(e) : null)}
        />
      </Button>
      <Button variant='contained' component='label'>
        Upload Resume
        <input
          type='file'
          name='resume'
          hidden
          onChange={(e) => (e.target ? uploadFile(e) : null)}
        />
      </Button>
      <TextField value={props.userInfo.imagePath}></TextField>
    </div>
  );
};

export default ShowInfo;
