class ViewJobs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [],
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8080/jobs").then((response) => {
      this.setState({
        jobs: response.data.jobs,
      });
    });
  }

  render() {
    return (
      <div>
        <h1>View Jobs</h1>
        {this.state.jobs.map((job) => {
          return <p key={job._id}>{job.title}</p>;
        })}
      </div>
    );
  }
}

export default ViewJobs;
