import React, { useState } from "react";
import { TextField } from "@material-ui/core";
import axios from "axios";

const ShowInfoR = (props) => {
  axios.defaults.withCredentials = true;
  const [userInfo, setUserInfo] = useState(props.userInfo);
  const updateFullInfo = () => {
    axios
      .post("http://localhost:8080/updateUserInfo", {
        userInfo,
        type: props.userType,
      })
      .then((response) => alert("Updated information"))
      .catch((err) => {
        console.log(err);
        alert("Couldnt update");
      });
  };
  const updateInfo = (event) => {
    setUserInfo((prevValues) => {
      return {
        ...prevValues,
        [event.target.name]: event.target.value,
      };
    });
  };
  return (
    <div>
      <TextField
        name='name'
        value={userInfo.name}
        label='Name'
        onChange={updateInfo}
      ></TextField>
      <br />
      <TextField
        name='email'
        label='Email'
        value={userInfo.email}
        onChange={updateInfo}
      ></TextField>{" "}
      <br />
      <TextField
        name='contactNo'
        label='Contact Number'
        value={userInfo.contactNo}
        onChange={updateInfo}
      ></TextField>{" "}
      <br />
    </div>
  );
};

export default ShowInfoR;
