class ViewJobs extends React.Component {
  constructor(props) {
    super(props);
    this.maxSalary = 0;
    this.state = {
      jobs: [],
      sortChoice: "salary",
      order: 1,
      search: "",
      filterType: "None",
      filterSalary: [0, 0],
      filterDuration: 7,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSlider = this.handleSlider.bind(this);
  }

  componentDidMount() {
    axios.get("http://localhost:8080/jobs").then((response) => {
      for (var i = 0; i < response.data.jobs.length; i++) {
        this.maxSalary = Math.max(this.maxSalary, response.data.jobs[i].salary);
      }
      this.setState({
        jobs: response.data.jobs,
        filterSalary: [0, this.maxSalary],
      });
    });
  }
  valuetext(value) {
    return `${value}`;
  }

  displayFilters() {
    let durationFilters = [];
    let jobTypeFilters = ["None", "Full Time", "Part Time", "Work from Home"];
    let typeFilters = [];
    for (var i = 1; i < 8; i++) {
      durationFilters.push(
        <MenuItem key={i} value={i}>
          {i}
        </MenuItem>
      );
    }

    for (i = 0; i < jobTypeFilters.length; i++) {
      typeFilters.push(
        <MenuItem key={i} value={jobTypeFilters[i]}>
          {jobTypeFilters[i]}
        </MenuItem>
      );
    }

    return (
      <div>
        <TextField
          style={{ margin: "20px" }}
          name='filterDuration'
          select
          label='Duration'
          value={this.state.filterDuration}
          onChange={this.handleChange}
        >
          {durationFilters}
        </TextField>
        <TextField
          style={{ margin: "20px" }}
          name='filterType'
          select
          label='Job Type'
          value={this.state.filterType}
          onChange={this.handleChange}
        >
          {typeFilters}
        </TextField>
        <div style={{ width: 300 }}>
          <Slider
            value={this.state.filterSalary}
            onChange={this.handleSlider}
            min={0}
            max={this.maxSalary}
            step={this.maxSalary / 10}
          />
        </div>
      </div>
    );
  }

  displayJobs() {
    this.state.jobs.sort(
      (a, b) =>
        (a[this.state.sortChoice] - b[this.state.sortChoice]) *
        parseInt(this.state.order)
    );
    return this.state.jobs
      .filter((job) => job.title.includes(this.state.search))
      .filter((job) => job.duration < this.state.filterDuration)
      .filter((job) =>
        this.state.filterType === "None"
          ? true
          : job.jobType === this.state.filterType
      )
      .map((job) => {
        return (
          <p key={job._id}>
            title: {job.title} salary: {job.salary} rating: {job.rating}{" "}
            duration: {job.duration}
          </p>
        );
      });
  }
  handleChange(event) {
    this.setState({
      ...this.state,
      [event.target.name]: event.target.value,
    });
  }
  handleSlider(event, newValue) {
    this.setState({
      ...this.state,
      filterSalary: newValue,
    });
  }

  render() {
    return (
      <div>
        <h1>View Jobs</h1>
        <TextField
          name='sortChoice'
          select
          label='Sort By'
          value={this.state.sortChoice}
          onChange={this.handleChange}
        >
          <MenuItem key='salary' value='salary'>
            Salary
          </MenuItem>
          <MenuItem key='rating' value='rating'>
            Rating
          </MenuItem>
          <MenuItem key='duration' value='duration'>
            Duration
          </MenuItem>
        </TextField>
        <TextField
          name='order'
          select
          label='Order'
          value={this.state.order}
          onChange={this.handleChange}
        >
          <MenuItem key='ascending' value='1'>
            Ascending
          </MenuItem>
          <MenuItem key='descending' value='-1'>
            Descending
          </MenuItem>
        </TextField>
        <TextField
          name='search'
          lable='Search Jobs'
          value={this.state.search}
          onChange={this.handleChange}
          autoComplete='off'
        ></TextField>
        {this.displayFilters()}
        {this.displayJobs()}
      </div>
    );
  }
}

export default ViewJobs;
