class LoggedIn extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8080/currUser")
      .then((response) => {
        this.setState({
          username: response,
        });
      })
      .catch((err) => {
        alert("Not logged In");
        this.props.history.push("/login");
      });
  }
  render() {
    return (
      <div>
        {this.state.username === "" ? (
          <h1>Loading Username</h1>
        ) : (
          <h1>{this.state.username}</h1>
        )}
      </div>
    );
  }
}
export default LoggedIn;
