import { Rating } from "@material-ui/lab";
import axios from "axios";
import React, { useState } from "react";
import { Redirect } from "react-router-dom";
import AddIcon from "@material-ui/icons/Add";
import DateFnsUtils from "@date-io/date-fns";
import { DatePicker, MuiPickersUtilsProvider } from "@material-ui/pickers";
import { makeStyles } from "@material-ui/core/styles";
import ShowInstance from "../ShowInstance/ShowInstance";
import ShowSkills from "../ShowSkills/ShowSkills";
import EditIcon from "@material-ui/icons/Edit";
import DoneIcon from "@material-ui/icons/Done";
import {
  TextField,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  InputAdornment,
} from "@material-ui/core";

const ShowInfo = (props) => {
  const [eduOpen, setEduOpen] = useState(false);
  const [skillOpen, setSkillOpen] = useState(false);
  const [eduInfo, setEduInfo] = useState({
    instituteName: "",
    startYear: new Date(Date.now()).getFullYear(),
    endYear: null,
  });
  const [isEditable, setIsEditable] = useState({
    name: false,
    email: false,
  });
  const [skillInfo, setSkillInfo] = useState("");

  axios.defaults.withCredentials = true;
  const [userInfo, setUserInfo] = useState(props.userInfo);

  function handleEduOpen() {
    setEduOpen(true);
  }
  function handleEduClose() {
    setEduOpen(false);
  }
  function handleSkillOpen() {
    setSkillOpen(true);
  }
  function handleSkillClose() {
    setSkillOpen(false);
  }
  function addEducation() {
    setUserInfo((prevValues) => {
      return {
        ...prevValues,
        education: [...prevValues.education, eduInfo],
      };
    });
    handleEduClose();
  }
  const useStyles = makeStyles((theme) => ({
    button: {
      marginTop: "20px",
    },
  }));
  const classes = useStyles();

  const uploadFile = (event) => {
    let type = event.target.name;
    let filType = event.target.files[0].type;
    if (type === "Image")
      setUserInfo((prevValues) => {
        return { ...prevValues, ImagePath: event.target.files[0].name };
      });
    else
      setUserInfo((prevValues) => {
        return { ...prevValues, resumePath: event.target.files[0].name };
      });
    if (
      type === "Image" &&
      filType !== "image/jpeg" &&
      filType !== "image/png"
    ) {
      alert("Only jpeg and png image allowed");
    } else {
      let data = new FormData();
      data.append("file", event.target.files[0]);
      data.append("type", type);
      axios
        .post("http://localhost:8080/storeFile", data, {
          headers: { "Content-Type": "multipart/form-data" },
        })
        .then((response) => alert(type + "Uploaded"))
        .catch((err) => {
          alert("Couldn't upload ");
          console.log(err);
        });
      // window.location.reload();
    }
  };
  const downloadFile = (type) => {
    let filename;
    if (type === "Image") filename = userInfo.ImagePath;
    else filename = userInfo.resumePath;
    axios({
      method: "POST",
      url: "http://localhost:8080/getFile",
      responseType: "blob",
      data: { filename },
    })
      .then((response) => {
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", filename); //or any other extension
        document.body.appendChild(link);
        link.click();
      })
      .catch((err) => {
        console.log(err);
        <Redirect to='/login' />;
      });
  };

  const addSkill = () => {
    setUserInfo((prevValues) => {
      return {
        ...prevValues,
        skills: [...prevValues.skills, { skillName: skillInfo }],
      };
    });
    setSkillInfo("");
    handleSkillClose();
  };

  const updateInfo = (event) => {
    setUserInfo((prevValues) => {
      return {
        ...prevValues,
        [event.target.name]: event.target.value,
      };
    });
  };

  const updateEduInfo = (event) => {
    setEduInfo((prevValues) => {
      return {
        ...prevValues,
        [event.target.name]: event.target.value,
      };
    });
  };

  const updateFullInfo = () => {
    axios
      .post("http://localhost:8080/updateUserInfo", {
        userInfo,
        type: props.userType,
      })
      .then((response) => alert("Updated information"))
      .catch((err) => {
        console.log(err);
        alert("Couldnt update");
      });
  };
  const showSkills = () => {
    return (
      <div>
        <Button
          variant='contained'
          color='primary'
          className={classes.button}
          startIcon={<AddIcon />}
          onClick={handleSkillOpen}
        >
          Add Skill
        </Button>
        <Dialog
          open={skillOpen}
          onClose={handleSkillClose}
          aria-labelledby='form-dialog-title'
        >
          <DialogTitle id='form-dialog-title'>Add Skill</DialogTitle>
          <DialogContent>
            <TextField
              autoFocus
              value={skillInfo}
              onChange={(e) => setSkillInfo(e.target.value)}
              required
              margin='dense'
              label='Skill'
              type='text'
              fullWidth
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleSkillClose} color='primary'>
              Cancel
            </Button>
            <Button onClick={addSkill} color='primary'>
              Add
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  };
  const showEducation = () => {
    return (
      <div>
        <Button
          variant='contained'
          color='primary'
          className={classes.button}
          startIcon={<AddIcon />}
          onClick={handleEduOpen}
        >
          Add Education
        </Button>
        <Dialog
          open={eduOpen}
          onClose={handleEduClose}
          aria-labelledby='form-dialog-title'
        >
          <DialogTitle id='form-dialog-title'>Education Instance</DialogTitle>
          <DialogContent>
            <TextField
              autoFocus
              name='instituteName'
              value={eduInfo.instituteName}
              onChange={updateEduInfo}
              required
              margin='dense'
              label='Institution Name'
              type='text'
              fullWidth
            />
            <MuiPickersUtilsProvider utils={DateFnsUtils}>
              <DatePicker
                views={["year"]}
                name='startYear'
                required
                label='Select Start Year'
                value={new Date(eduInfo.startYear, 11, 0)}
                onChange={(date) =>
                  setEduInfo((prevValues) => {
                    return { ...prevValues, startYear: date.getFullYear() };
                  })
                }
                style={{ marginTop: "10px" }}
                fullWidth
                animateYearScrolling
              />
              <DatePicker
                views={["year"]}
                name='endYear'
                label='Select End Year'
                disabled
                value={
                  eduInfo.endYear === ""
                    ? Date.now()
                    : new Date(eduInfo.endYear, 11, 0)
                }
                minDate={new Date(eduInfo.startYear, 11, 0)}
                onChange={(date) =>
                  setEduInfo((prevValues) => {
                    return { ...prevValues, endYear: date.getFullYear() };
                  })
                }
                style={{ marginTop: "10px" }}
                fullWidth
                animateYearScrolling
              />
            </MuiPickersUtilsProvider>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleEduClose} color='primary'>
              Cancel
            </Button>
            <Button onClick={addEducation} color='primary'>
              Add
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  };
  const deleteEdu = (id) => {
    let newEduArray = userInfo.education.filter(
      (instance) => instance._id !== id
    );
    setUserInfo((prevValues) => {
      return { ...prevValues, education: newEduArray };
    });
  };
  const deleteSkill = (id) => {
    let newSkillArray = userInfo.skills.filter((skill) => skill._id !== id);
    setUserInfo((prevValues) => {
      return { ...prevValues, skills: newSkillArray };
    });
  };
  const displayEducation = () => {
    // Title, date of joining, salary per month, name of recruiter.
    // Date of joining?
    return userInfo.education.map((instance) => {
      return (
        <ShowInstance
          data={instance}
          id={instance._id}
          key={instance._id}
          deleteEdu={deleteEdu}
        />
      );
    });
  };
  const displaySkills = () => {
    return userInfo.skills.map((skill) => {
      return (
        <ShowSkills
          data={skill.skillName}
          id={skill._id}
          key={skill._id}
          deleteSkill={deleteSkill}
        />
      );
    });
  };

  const updateIsEditable = (field) => {
    setIsEditable((prevValues) => {
      return {
        ...prevValues,
        [field]: !prevValues[field],
      };
    });
  };

  return (
    <div>
      <img
        src={process.env.PUBLIC_URL + "/assets/profilePic.png"}
        style={{ width: 100 }}
        alt='profilePic'
      />
      <br />
      <TextField
        name='name'
        value={userInfo.name}
        label='Name'
        onChange={(e) => (isEditable.name ? updateInfo(e) : null)}
        InputProps={{
          endAdornment: (
            <InputAdornment position='end'>
              <IconButton onClick={() => updateIsEditable("name")}>
                {isEditable.name ? <DoneIcon /> : <EditIcon />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      ></TextField>
      <br />
      <TextField
        name='email'
        label='Email'
        value={userInfo.email}
        onChange={(e) => (isEditable.email ? updateInfo(e) : null)}
        InputProps={{
          endAdornment: (
            <InputAdornment position='end'>
              <IconButton onClick={() => updateIsEditable("email")}>
                {isEditable.email ? <DoneIcon /> : <EditIcon />}
              </IconButton>
            </InputAdornment>
          ),
        }}
      ></TextField>{" "}
      <br />
      <Rating
        name='rating'
        value={Number(userInfo.rating)}
        onChange={updateInfo}
        defaultValue={props.userInfo.rating}
        precision={0.5}
      />{" "}
      <h1>{userInfo.rating}</h1>
      <br />
      <h3>Education Instances</h3>
      {displayEducation()}
      {showEducation()}
      <h3>Skills</h3>
      {displaySkills()}
      {showSkills()}
      <Button color='primary' onClick={updateFullInfo}>
        Update Information
      </Button>
      <Button variant='contained' component='label'>
        Upload Image
        <input
          type='file'
          name='Image'
          hidden
          onChange={(e) => (e.target ? uploadFile(e) : null)}
        />
      </Button>
      <Button variant='contained' component='label'>
        Upload Resume
        <input
          type='file'
          name='resume'
          hidden
          onChange={(e) => (e.target ? uploadFile(e) : null)}
        />
      </Button>
      <Button onClick={() => downloadFile("Image")}>Download Image</Button>
      <Button onClick={() => downloadFile("resume")}>Download Resume</Button>
    </div>
  );
};

export default ShowInfo;
