import React, { useState } from "react";
import AddIcon from "@material-ui/icons/Add";
import DateFnsUtils from "@date-io/date-fns";
import { DatePicker, MuiPickersUtilsProvider } from "@material-ui/pickers";
import { makeStyles } from "@material-ui/core/styles";
import {
  Grid,
  TextField,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from "@material-ui/core";

const JobFields = () => {
  const [eduOpen, setEduOpen] = useState(false);
  const [skillOpen, setSkillOpen] = useState(false);
  const [startYear, setStartYear] = useState(new Date());
  const [endYear, setEndYear] = useState(new Date());

  function handleEduOpen() {
    setEduOpen(true);
  }
  function handleEduClose() {
    setEduOpen(false);
  }
  function handleSkillOpen() {
    setSkillOpen(true);
  }
  function handleSkillClose() {
    setSkillOpen(false);
  }
  function addEducation() {
    handleEduClose();
  }
  const useStyles = makeStyles((theme) => ({
    button: {
      marginTop: "20px",
    },
  }));
  const classes = useStyles();
  return (
    <Grid item xs={12} sm={6}>
      <TextField required id='Name' type='text' label='Name'></TextField>
      <TextField required id='Email' type='email' label='Email'></TextField>
      <Button
        variant='contained'
        color='primary'
        className={classes.button}
        startIcon={<AddIcon />}
        onClick={handleEduOpen}
      >
        Add Education
      </Button>
      <Dialog
        open={eduOpen}
        onClose={handleEduClose}
        aria-labelledby='form-dialog-title'
      >
        <DialogTitle id='form-dialog-title'>Education Instance</DialogTitle>
        <DialogContent>
          <Grid container>
            <Grid item>
              <TextField
                autoFocus
                margin='dense'
                id='instName'
                label='Institution Name'
                type='text'
                fullWidth
              />
            </Grid>
            <MuiPickersUtilsProvider utils={DateFnsUtils}>
              <Grid item>
                <DatePicker
                  views={["year"]}
                  label='Select Start Year'
                  value={startYear}
                  onChange={setStartYear}
                  animateYearScrolling
                />
              </Grid>
              <Grid item>
                <DatePicker
                  views={["year"]}
                  label='Select End Year'
                  value={endYear}
                  onChange={setEndYear}
                  animateYearScrolling
                />
              </Grid>
            </MuiPickersUtilsProvider>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEduClose} color='primary'>
            Cancel
          </Button>
          <Button onClick={addEducation} color='primary'>
            Add
          </Button>
        </DialogActions>
      </Dialog>
    </Grid>
  );
};

const RecruiterFields = () => {
  return <h1>Here are recruiter fields</h1>;
};

const NoFields = () => {
  return <h1>No type selected</h1>;
};

const Fields = (props) => {
  if (props.type === "") return NoFields();
  else if (props.type === "Recruiter") return RecruiterFields();
  else return JobFields();
};

export default Fields;
