import React, { useState } from "react";
import axios from "axios";

const ViewJobs = () => {
  const [gotJobs, setJobs] = useState(false);
  let data;
  const displayJobs = (jobs) => {
    data = jobs.jobs.map((job) => {
      return (
        <div>
          <p>
            {job.title} {job.salary} {job.duration} {job.rating}
          </p>{" "}
          <br />
        </div>
      );
    });
  };

  axios.get("http://localhost:8080/jobs").then((response) => {
    displayJobs(response.data);
    setJobs(true);
  });
  if (gotJobs) {
    return data;
  } else {
    return <h1>View Jobs</h1>;
  }
};

export default ViewJobs;
