class ViewJobs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [],
      sortChoice: "salary",
      order: 1,
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8080/jobs").then((response) => {
      this.setState({
        jobs: response.data.jobs,
      });
    });
  }

  displayJobs() {
    this.state.jobs.sort(
      (a, b) =>
        (a[this.state.sortChoice] - b[this.state.sortChoice]) * this.state.order
    );
    return this.state.jobs.map((job) => {
      return (
        <p key={job._id}>
          title: {job.title} salary: {job.salary} rating: {job.rating} duration:{" "}
          {job.duration}
        </p>
      );
    });
  }
  handleChange(event) {
    this.setState({
      ...this.state,
      [event.target.name]: event.target.value,
    });
  }

  render() {
    return (
      <div>
        <h1>View Jobs</h1>
        <TextField
          name='sortChoice'
          select
          label='Sort By'
          value={this.state.sortChoice}
          onChange={this.handleChange}
        >
          <MenuItem key='salary' value='salary'>
            Salary
          </MenuItem>
          <MenuItem key='rating' value='rating'>
            Rating
          </MenuItem>
        </TextField>
        {this.displayJobs()}
      </div>
    );
  }
}

export default ViewJobs;
