class ViewJobs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [],
      sortChoice: "salary",
      order: 1,
      search: "",
      filterType: "None",
      filterSalary: 0,
      filterDuration: "7",
    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    axios.get("http://localhost:8080/jobs").then((response) => {
      this.setState({
        jobs: response.data.jobs,
      });
    });
  }

  displayFilters() {
    let durationFilters = [];
    let jobTypeFilters = ["None", "Full Time", "Part Time", "Work from Home"];
    let typeFilters = [];
    for (var i = 1; i < 8; i++) {
      durationFilters.push(
        <MenuItem key='duration' value={i}>
          {i}
        </MenuItem>
      );
    }

    for (i = 0; i < jobTypeFilters.length; i++) {
      typeFilters.push(
        <MenuItem key='jobType' value={jobTypeFilters[i]}>
          {jobTypeFilters[i]}
        </MenuItem>
      );
    }

    return (
      <div>
        <TextField
          style={{ margin: "20px" }}
          name='filterDuration'
          select
          label='Duration'
          value={this.state.filterduration}
          onChange={this.handleChange}
        >
          {durationFilters}
        </TextField>
        <TextField
          style={{ margin: "20px" }}
          name='filterType'
          select
          label='Job Type'
          value={this.state.filterType}
          onChange={this.handleChange}
        >
          {typeFilters}
        </TextField>
      </div>
    );
  }

  displayJobs() {
    this.state.jobs.sort(
      (a, b) =>
        (a[this.state.sortChoice] - b[this.state.sortChoice]) *
        parseInt(this.state.order)
    );
    return this.state.jobs
      .filter((job) => job.title.includes(this.state.search))
      .filter((job) => job.duration < this.state.filterDuration)
      .filter((job) =>
        this.state.filterType === "None"
          ? true
          : job.jobType === this.state.filterType
      )
      .map((job) => {
        return (
          <p key={job._id}>
            title: {job.title} salary: {job.salary} rating: {job.rating}{" "}
            duration: {job.duration}
          </p>
        );
      });
  }
  handleChange(event) {
    this.setState({
      ...this.state,
      [event.target.name]: event.target.value,
    });
    console.log(this.state.search);
  }

  render() {
    return (
      <div>
        <h1>View Jobs</h1>
        <TextField
          name='sortChoice'
          select
          label='Sort By'
          value={this.state.sortChoice}
          onChange={this.handleChange}
        >
          <MenuItem key='salary' value='salary'>
            Salary
          </MenuItem>
          <MenuItem key='rating' value='rating'>
            Rating
          </MenuItem>
          <MenuItem key='duration' value='duration'>
            Duration
          </MenuItem>
        </TextField>
        <TextField
          name='order'
          select
          label='Order'
          value={this.state.order}
          onChange={this.handleChange}
        >
          <MenuItem key='ascending' value='1'>
            Ascending
          </MenuItem>
          <MenuItem key='descending' value='-1'>
            Descending
          </MenuItem>
        </TextField>
        <TextField
          name='search'
          lable='Search Jobs'
          value={this.state.search}
          onChange={this.handleChange}
          autoComplete='off'
        ></TextField>
        {this.displayFilters()}
        {this.displayJobs()}
      </div>
    );
  }
}

export default ViewJobs;
