class ViewJobs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [],
      sortChoice: "salary",
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8080/jobs").then((response) => {
      this.setState({
        jobs: response.data.jobs,
      });
    });
  }

  displayJobs() {
    this.state.jobs.sort((a, b) => a.salary - b.salary);
    return this.state.jobs.map((job) => {
      return (
        <p key={job._id}>
          title: {job.title} salary: {job.salary} rating: {job.rating} duration:{" "}
