import React from "react";
import axios from "axios";
import { Button } from "@material-ui/core";
import ShowInfo from "../ShowInfo/ShowInfo";
import profilePic from "../../images/profilePic.png";

class LoggedIn extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currUser: {},
      currUserInfo: {},
      image: profilePic,
    };
    this.logout = this.logout.bind(this);
    this.displayProfilePic = this.displayProfilePic.bind(this);
  }

  componentDidMount() {
    axios.defaults.withCredentials = true;
    axios
      .get("http://localhost:8080/currUser")
      .then((response) => {
        let obj = response.data;
        if (Object.entries(obj).length === 0) {
          alert("Not logged In");
          this.props.history.push("/login");
        } else {
          this.setState({
            currUser: obj.currUser,
            currUserInfo: obj.currUserInfo,
          });
          axios
            .post("http://localhost:8080/getFile", { type: "Image" })
            .then((response) => {
              console.log(response.data);
              this.setState({ image: response.data });
            })
            .catch((err) => console.log(err));
        }
      })
      .catch((err) => {
        alert("Not logged In");
        this.props.history.push("/login");
      });
  }

  logout() {
    axios.defaults.withCredentials = true;
    axios
      .get("http://localhost:8080/logout")
      .then((response) => {
        alert("Logout Sucessful");
        this.props.history.push("/");
      })
      .catch((err) => {
        alert("Logout Failed");
        console.log(err);
      });
  }
  displayProfilePic() {
    return (
      <div>
        <img src={this.state.image} alt='profilePic' style={{ width: 100 }} />
        <br />
      </div>
    );
  }
  render() {
    if (Object.entries(this.state.currUser).length === 0)
      return <h1>Loading...</h1>;
    else {
      return (
        <div>
          <Button onClick={() => this.props.history.push("/viewJobs")}>
            View Job Listings
          </Button>
          <Button onClick={() => this.props.history.push("/myApplications")}>
            My Applications
          </Button>
          <Button onClick={this.logout}>Logout</Button>
          <h1>{this.state.currUser.username}</h1>
          {this.displayProfilePic()}
          <ShowInfo userInfo={this.state.currUserInfo} />
        </div>
      );
    }
  }
}
export default LoggedIn;
