class ViewJobs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jobs: [],
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8080/jobs").then((response) => {
      this.setState({
        jobs: response.data.jobs,
      });
    });
  }

  displayJobs() {
    this.state.jobs.sort((a, b) => a.salary - b.salary);
    return this.state.jobs.map((job) => {
      return (
        <p key={job._id}>
          title: {job.title} salary: {job.salary} rating: {job.rating} duration:{" "}
          {job.duration}
        </p>
      );
    });
  }

  render() {
    return (
      <div>
        <h1>View Jobs</h1>
        <TextField
          name='profileType'
          select
          label='Type'
          value={profileInfo.profileType}
          onChange={handleChange}
          helperText='Please select your type'
        >
          <MenuItem key='JobApplicant' value='JA'>
            Job Applicant
          </MenuItem>
          <MenuItem key='Recruiter' value='R'>
            Recruiter
          </MenuItem>
        </TextField>
        {this.displayJobs()}
      </div>
    );
  }
}

export default ViewJobs;
